var AdminRegistration = angular.module('ivNet.Admin.Registration', ['ngResource', 'trNgGrid'])
.filter("registrationDateField", function () {
    return function (combinedFieldValueUnused, item) {
        var d = item.CreateDate;

        var curr_date = d.substr(8, 2);
        var curr_month = d.substr(5, 2);
        var curr_year = d.substr(0, 4);

        return curr_date + "/" + curr_month + "/" + curr_year;
    };
});

AdminRegistration.factory('registration', function ($resource) {
    return $resource('/api/club/registration/:id', null,
    {
        'query': { method: 'Get', isArray: true },
        'update': { method: 'PUT' }
    });
});

AdminRegistration.controller('AdminRegistrationController', function ($scope, $filter, registration) {
   
    $scope.vetMember = function (item) {

        var memberItemIndex = -1;
        $.each($scope.registrations, function (memberIndex, memberItem) {
            if (memberItem.Id == item.Id) {
                memberItemIndex = memberIndex;
            }
        });

        var data = {
            Action: "vet"
        };

        registration.update({ id: item.MemberId }, data,
            function () {
                $scope.registrations.splice(memberItemIndex-1, 1);
            },
            function (error) {
                alert(error.data.Message + ' [' + error.data.MessageDetail + ']');
            });
    };

    init();

    function init() {

        $('#loading-indicator').show();
        registration.query({},
            function (data) {
                $scope.show_registration_fields = ['Name', 'CreateDate'];
                $scope.registrations = data;
                $('#loading-indicator').hide();
            },
            function (error) {
                $('#loading-indicator').hide();
                alert(error.data.Message + ' [' + error.data.MessageDetail + ']');
            });

    };
});

AdminRegistration.controller('HelpController', function ($scope) {
    $scope.showModal = false;
    $scope.toggleModal = function () {
        $scope.showModal = !$scope.showModal;
    };
});

AdminRegistration.directive('modal', function () {
    return {
        template: '<div class="modal fade">' +
            '<div class="modal-dialog">' +
              '<div class="modal-content">' +
                '<div class="modal-header">' +
                  '<button type="button" class="close" data-dismiss="modal" aria-hidden="true">&times;</button>' +
                  '<h4 class="modal-title">{{ title }}</h4>' +
                '</div>' +
                '<div class="modal-body" ng-transclude></div>' +
              '</div>' +
            '</div>' +
          '</div>',
        restrict: 'E',
        transclude: true,
        replace: true,
        scope: true,
        link: function postLink(scope, element, attrs) {
            scope.title = attrs.title;

            scope.$watch(attrs.visible, function (value) {
                if (value == true)
                    $(element).modal('show');
                else
                    $(element).modal('hide');
            });

            $(element).on('shown.bs.modal', function () {
                scope.$apply(function () {
                    scope.$parent[attrs.visible] = true;
                });
            });

            $(element).on('hidden.bs.modal', function () {
                scope.$apply(function () {
                    scope.$parent[attrs.visible] = false;
                });
            });
        }
    };
});